import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom';
import logo from '../../assest/logo.png'


const SignupFeatures = () => {
  const [firstName, setfirstName] = useState('');
  const [lastName, setlastName] = useState('');
  const [email, setemail] = useState('');
  const [password, setpassword] = useState('');
  const [confirmpassword, setconfirmpassword] = useState('');
  const [data, setData] = useState();

  const onHandleClick = async () => {
    if (firstName && lastName && email && password && confirmpassword) {
      await fetch("http://localhost:6001/user/signup", {
        method: 'POST',
        body: JSON.stringify({ firstName, lastName, email, password, confirmpassword }),
        headers: {
          'Content-Type':'applicaton/json'
        }
      }).then(response => response.json()).then
    }
  }



  return (

        <div className='p-3 md:p-4'>
          <div className='w-full max-w-sm bg-white m-auto flexflex-col p-4'>
            <div className='w-20 h-20 overflow-hidden rounded-full drop-shadow-md shadow-md m-auto relative'>
              <img src={logo} className='w-full h-full ' />
              <label htmlFor='profileImage'>
                <input type={"file"} id='profileImage' accept='image/*' className='hidden' onChange={e => setData(e.target.files[0])} />
              </label>
            </div>
    
            <form className='w-full py-3 flex flex-col' >
              <label htmlFor='firstName'>First Name</label>
              <input type={"text"}
                id='firstName'
                name='firstName'
                className='mt-1 mb-2 w-full bg-slate-200 px-2 py-1 rounded focus-within:outline-blue-300' />
    
              <label htmlFor='lastName'>Last Name</label>
              <input type={"text"}
                id='lastName'
                name='lastName'
                className='mt-1 mb-2 w-full bg-slate-200 px-2 py-1 rounded focus-within:outline-blue-300' />
    
              <label htmlFor='email'>Email ID</label>
              <input type={"text"}
                id='email' name='email'
                className='mt-1 mb-2 w-full bg-slate-200 px-2 py-1 rounded focus-within:outline-blue-300' />
    
              <label htmlFor='password'>Password</label>
              <div className='flex px-2 py-1 bg-slate-200  mt-1 mb-2  rounded focus-within:outline focus-within:outline-blue-300'>
                <input type={"password"}
                  id='password'
                  name='password'
                  className=' w-full  bg-slate-200  border-none outline-none' />
              </div>
    
              <label htmlFor='confrimpassword'>Confrim Password</label>
              <div className='flex px-2 py-1 bg-slate-200  mt-1 mb-2  rounded focus-within:outline focus-within:outline-blue-300'>
                <input type={"password"}
                  id='confrimpassword'
                  name='confrimpassword'
                  className=' w-full  bg-slate-200  border-none outline-none' />
              </div>

              <label htmlFor='lastName'>Phone Number</label>
              <input type={"text"}
                id='phonenumber'
                name='phonenumber'
                className='mt-1 mb-2 w-full bg-slate-200 px-2 py-1 rounded focus-within:outline-blue-300' />
    
              <button type='submit' onClick={onHandleClick}
                className='w-full max-w-[150px] m-auto bg-red-500 hover:bg-red-600 cursor-pointer text-white text-xl font-medium text-center py-1 rounded-full mt-4'>
                Sign Up
              </button>
            </form>
            <p className='text-left text-sm mt-2'>Already Have An Account??
              <Link to={"/signin"}  className='text-red-500 underline'>
                LogIN
              </Link>
            </p>
          </div>
        </div>

      )
    }
    


export default SignupFeatures
